    void DeleteMateria()
    {
        using (ProgramaEstudiantilDBContext cmd_Delete = new ProgramaEstudiantilDBContext())
        {
            if (materia.MateriaId == 0)
            {
                Mensaje = "Debe Lenar el Campo Cedula!";
            }
